import * as CONSTANT  from '../constant/index';
const InitialState = false;
const loading  = (state = InitialState, action) => {
    switch (action.type) {
        case CONSTANT.CHANGE_LOADING_STATUS:
            state = true
            return {...state}
        default:
            return  {...state}
    }
}
export default loading;
