import {fork,put} from 'redux-saga/effects';
import {change_loading_status} from '../action/loading';
import {getMethod} from '../api/index';

function* getListProductsFromFirebase(){
    yield put(change_loading_status());
    let data = yield call(getList,params);
  
}





function * rootSaga (){
    yield fork(getListProductsFromFirebase);
}

export default rootSaga;
