import * as CONSTANT from '../constant/index';

const initState = {

}

const itemadd = (state = initState, action) => {
    switch (action.type) {
    case CONSTANT.PUSH_ITEM_TO_STORE:
       const {item}  = action.payload;
       state = item;
       console.log(state);
       return {...state};

           
     
    
   
        default:
            return state
    }
}

