import firebase from '../firebaseConfig';
export const  getMethod = () =>{
    firebase.firestore().collection("listproduct").get().then((querySnapshot) => {
        querySnapshot.forEach((doc) => {
            console.log(doc.data());
        });
     
    }).catch((erro) =>{
  
        console.log(erro)
    })
}
