}
else{
  console.log('sai');
  console.log(loadingStatus.loading);
}

  }







  render() {

      return(
    <div className="App">
      <Router>
        {this.showLoadingAnimation()}
      <Header />
    
     
          <Switch>
       {this.printRouter()}   
       </Switch>

       <Footer />  
       </Router>
    </div>
            );
          }
      }
      const mapStateToProps = (state, ownProps) => {
        return {
            loadingStatus : state.loading
        }
      }
