import React, { Component } from 'react';
import firebase from '../firebaseConfig';

export class Admin extends Component {
    constructor(props) {
        super(props);
        this.state = {};
    }

    uploadfile = (e) =>{
        if(e.target.files[0]){
           const filechoice = e.target.files[0];
           this.setState(
               () => ({filechoice})
           );
        }
   
    }

onsubmitForm = () =>{
    const {filechoice} = this.state.filechoice;
    const storeage = firebase.storage();
    const storageRef = storeage.ref(`/shopimage/${filechoice.name}`);
    console.log(storageRef);
 
    
}


    render() {
        return (
                    <div className="container">
                <div className="form-group">
                  <label>Tai anh len</label>
                  <input type="file" className="form-control-file" name="" id="" placeholder="" aria-describedby="fileHelpId" onChange={(e) => this.uploadfile(e)}/>
                  
                </div>
                        <button onClick={this.onsubmitForm} type="submit" className="btn btn-success">submit</button>

                </div>

        )
    }
}

export default Admin
